mapper.select:
SELECT * from s_user where is_deleted = 0
and username like concat("%",#{username},"%")
and class_id like concat("%",#{classId},"%")
and id = #{studentId}
limit #{startIndex}, #{pagesize}

SELECT * from s_user where is_deleted = 0 and user_type = 2
and username like concat("%",#{username},"%")
and class_id like concat("%",#{classId},"%")
and id = #{studentId}
limit #{startIndex}, #{pagesize}

select DISTINCT su.*
from s_user su
         INNER JOIN s_selected_course ssc on su.id = ssc.student_id and ssc.teacher_id = #{teacherId}

    and username like concat("%",#{username},"%")


    and class_id like concat("%",#{classId},"%")


    and id = #{studentId}

limit #{startIndex}, #{pagesize}

SELECT * from s_user where is_deleted = 0 and user_type = 3
and username like concat("%",#{username},"%")
and class_id like concat("%",#{classId},"%")
and id = #{teacherId}
limit #{startIndex}, #{pagesize}

select DISTINCT count(*)
from s_user su
INNER JOIN s_selected_course ssc on su.id = ssc.student_id and ssc.teacher_id = #{teacherId}

    and username like concat("%",#{username},"%")


    and class_id like concat("%",#{classId},"%")


    and id = #{studentId}

limit #{startIndex}, #{pagesize}
